const words = wordsString.split(' ')
const textP = document.querySelector('p#text')

//word state
let wordsIndex
let currentWordsIndex

//setup text input
const input = document.querySelector('input#input')

//setup countdown
const countdownP = document.querySelector('p#countdown')
let countdown
let countdownInterval

//highscore state
let wordsTyped
let correctWordsTyped

function defaultValues() {
  countdown = 60

  wordsIndex = 0
  currentWordsIndex = 1

  wordsTyped = 0
  correctWordsTyped = 0

  countdownP.textContent = countdown
}

function idleState() {
  defaultValues()
  reloadText()
  selectNextWord()

  input.setAttribute('placeholder', 'Type to begin.')
  input.addEventListener('keyup', gameState)
}

function gameState() {
  const infoH3 = document.querySelector('h3#info')
  infoH3.textContent = "Game On!"

  input.removeAttribute('placeholder')
  input.removeEventListener('keyup', gameState)

  //calling reloadCountdown once before set interval to avoid delay
  reloadCountdown()
  countdownInterval = setInterval(reloadCountdown, 1000)


  input.addEventListener('keyup', textInput)
}

function reloadText() {
  const currentWords = words.slice(wordsIndex, wordsIndex + 5)

  currentWords.forEach(word => {
    const span = document.createElement('span')
    span.textContent = word + ' '

    textP.appendChild(span)
  })

  currentWordsIndex = 1
}

//inserts the next text to be typed
function reloadCountdown() {
  countdownP.textContent = countdown

  countdown--

  if (countdown < 0) {
    clearInterval(countdownInterval)
    timesUp()
  }
}

function timesUp() {
  //remove focus and event listener for text input
  input.blur()
  input.removeEventListener('keyup', textInput)

  const infoh3 = document.querySelector('h3#info')
  infoh3.textContent = 'Times up!'


  //update highscore table
  const newScoreRow = document.createElement('tr')

  const newScoreSubmitted = document.createElement('td')
  const date = new Date()
  const day = date.getDate() < 10 ? '0' + date.getDate() : date.getDate()
  const month = date.getMonth() < 10 ? '0' + date.getMonth() : date.getMonth()
  const year = date.getFullYear()
  newScoreSubmitted.textContent = `${day}/${month}/${year}`

  const newScoreWordsTyped = document.createElement('td')
  newScoreWordsTyped.textContent = wordsTyped

  const newScoreCorrectWordsTyped = document.createElement('td')
  newScoreCorrectWordsTyped.textContent = correctWordsTyped

  newScoreRow.appendChild(newScoreSubmitted)
  newScoreRow.appendChild(newScoreWordsTyped)
  newScoreRow.appendChild(newScoreCorrectWordsTyped)

  const highscoreTableBody = document.querySelector('table#highscore tbody')
  highscoreTableBody.insertBefore(newScoreRow, highscoreTableBody.firstChild)

  removeTextChildren()
  idleState()
}

function textInput(event) {
  const key = event.key

   if (key === ' '){
    wordsTyped++

    //collects the typed word into a string
    const typedWord = input.value.trim()
    const isCorrect = typedWord === words[wordsIndex]
    if (isCorrect) {
      correctWordsTyped++
    }

    //attaches the correct class to a word span
    const wordClass = isCorrect ? 'correct' : 'incorrect'
    const currentWordSpan = document.querySelector('p#text span:nth-child(' + currentWordsIndex + ')')
    currentWordSpan.classList.add(wordClass)

    //resets the state
    input.value = ''

    //advances the current words
    advanceWords()
  }
}

function advanceWords() {
  currentWordsIndex++
  wordsIndex++

  if (currentWordsIndex > 5) {
    currentWordsIndex = 1
    removeTextChildren()
    reloadText()
  }

  selectNextWord()
}

//will remove the old selection and add the new one
function selectNextWord() {
  const previousSelection = document.querySelector('span.selected')
  if (previousSelection) {
    previousSelection.classList.remove('selected')
  }
  document.querySelector('p#text span:nth-child(' + currentWordsIndex + ')').classList.add('selected')
}

//removes the old text
function removeTextChildren() {
  textP.querySelectorAll('*').forEach(n => n.remove())
}